import React, { Fragment, useState, useEffect } from "react";
const SearchBar = ({keyword, onChange}) => {
    
    return (
    <Fragment>
      <h4 style={{fontFamily: 'Source Sans Pro', display: 'inline-flex'}}>Search Todo:</h4>
      <input className="d-flex form-control"
       style={{fontFamily: 'Source Sans Pro', width: '350px'}}
       key="search-bar"
       value={keyword}
       placeholder={"Search"}
       onChange={(e) => onChange(e.target.value)}
      />
    </Fragment>
    );
  }
  
  export default SearchBar;
