import React, { Fragment, useState, useEffect } from "react";
const SearchBar = ({keyword, onChange}) => {
    // const BarStyle = {fontFamily: 'Source Sans Pro'};
    return (
    <Fragment>
      <input 
    //    style={BarStyle}
       key="search-bar"
       value={keyword}
       placeholder={"Search"}
       onChange={(e) => onChange(e.target.value)}
      />
    </Fragment>
    );
  }
  
  export default SearchBar;
