import React, { Fragment, useState, useEffect, useRef, useMemo } from "react";
import EditTodo from "./EditTodo";
import Checkbox from "./Checkbox";

const ListTodos = ({ allTodos, setTodosChange }) => {
  console.log(allTodos);
  const [todos, setTodos] = useState([]); //empty array

  //delete todo function

  async function deleteTodo(id) {
    try {
      await fetch(`http://localhost:5000/dashboard/todos/${id}`, {
        method: "DELETE",
        headers: { jwt_token: localStorage.token }
      });

      setTodos(todos.filter(todo => todo.todo_id !== id));
    } catch (err) {
      console.error(err.message);
    }
  }

  useEffect(() => {
    setTodos(allTodos);
  }, [allTodos]);

  console.log(todos);

  const [isChecked, setIsChecked] = useState(false);

  const checkTodoState = (idName) => {
    let checkBoxButton = document.getElementById(idName)
    console.log(checkBoxButton)
    return true

  }

  return (
    <Fragment>
      {" "}
      <table className="table mt-5">
        <thead>
          <tr>
            <th>To-do Tasks</th>
            <th>Edit Task</th>
            <th>Done</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {/* {} */}

          {todos.length !== 0 &&
            todos[0].todo_id !== null &&
            todos.map(todo => (
              <tr key={todo.todo_id}>
                <td>{checkTodoState(`checkBoxSpecial-${todo.todo_id}`)?<text style={{ textDecorationLine: 'line-through' }}>{todo.description}</text>:todo.description}</td>
                <td>
                  <EditTodo todo={todo} setTodosChange={setTodosChange} />
                </td>

                <td>
                  <Checkbox isChecked={isChecked} setIsChecked={setIsChecked} id={todo.todo_id}/>
                </td>

                <td>
                  <button
                    className="btn btn-danger" 
                   onClick={() => deleteTodo(todo.todo_id)}
                  >
                    {/* ㅤㅤ ✕ ㅤㅤ */}
                    ✕ 
                  </button>
                </td>
              </tr>
            ))}
        </tbody>
      </table>
    </Fragment>
  );
};


export default ListTodos;
