import React, { Fragment, useState, useEffect, useRef, useMemo, useCallback } from "react";
import * as MdIcons from "react-icons/md";
import EditTodo from "./EditTodo";
import styled from 'styled-components';
import Checkbox from "react-custom-checkbox";

const ListTodos = ({ allTodos, setTodosChange }) => {
  console.log(allTodos);
  const [todos, setTodos] = useState([]); //empty array

  //delete todo function

  async function deleteTodo(id) {
    try {
      await fetch(`http://localhost:5000/dashboard/todos/${id}`, {
        method: "DELETE",
        headers: { jwt_token: localStorage.token }
      });

      setTodos(todos.filter(todo => todo.todo_id !== id));
    } catch (err) {
      console.error(err.message);
    }
  }

  useEffect(() => {
    setTodos(allTodos);
  }, [allTodos]);

  console.log(todos);

  // edit todo status

  const handleEditTodoStatus = async (id, description) => {
    try {
      const body = { description };
      console.log(body)

      const myHeaders = new Headers();

      myHeaders.append("Content-Type", "application/json");
      myHeaders.append("jwt_token", localStorage.token);

      await fetch(`http://localhost:5000/dashboard/todos/${id}`, {
        method: "PUT",
        headers: myHeaders,
        body: JSON.stringify(body)
      });

      setTodosChange(true);

    } catch (err) {
      console.error(err.message);
    }
  }

  const checkboxIcon = <div
    style={{
        display: "flex",
        flex: 1,
        backgroundColor: "#207DEA",
        alignSelf: "stretch",
    }}>
    <MdIcons.MdCheck
        color="#FFFFFF"
        size={14}
    />
</div>

  return (
    <Fragment>
      {" "}
      <table className="table mt-5">
        <thead>
          <tr>
            <th>To-do Tasks</th>
            <th>Edit Task</th>
            <th>Delete</th>
            <th>Done</th>
          </tr>
        </thead>
        <tbody>

          {todos.length !== 0 &&
            todos[0].todo_id !== null &&
            todos.sort().map(todo => (

              <tr key={todo.todo_id}>
                <td>{todo.status ? <text style={{ textDecorationLine: 'line-through' }}>{todo.description}</text> : todo.description}</td>
                {/* <td>{todo.description}</td> */}
                <td>
                  <EditTodo todo={todo} setTodosChange={setTodosChange} />
                </td>

                <td>
                  <button
                    className="btn btn-danger" 
                   onClick={() => deleteTodo(todo.todo_id)}
                  >
                    {/* ㅤㅤ ✕ ㅤㅤ */}
                    {/* ✕  */}
                    <MdIcons.MdClose size={22}/>
                  </button>
                </td>

                <td>
                  <input style={{width: "40px",
                                height: "40px",
                                background: "#555",
                                position: "relative",
                                // borderRadius: 3,
                              }} 
                          type="checkbox" id={`specialCheckBox-${todo.todo_id}`} onChange={(e)=>{handleEditTodoStatus(todo.todo_id, e.target.checked)}} checked={todo.status}>
                  </input>
                  {/* <Checkbox
														label={todo.todo_id}
														icon={checkboxIcon}
														name="filter-checkboxes"
														checked={todo.status}
														borderColor="#AEB7C6"
														style={{
															cursor: "pointer",
															userSelect: "none",
															marginBottom: 10,
														}}
														labelStyle={{
															marginLeft: 10,
															marginBottom: 10,
															color: "#293241",
															fontSize: 14,
															fontFamily:
																"Source Sans Pro",
														}}
														onChange={(e)=>{handleEditTodoStatus(todo.todo_id, e.target.checked)}}
													/> */}
                </td>

              </tr>
            ))}
        </tbody>
      </table>
    </Fragment>
  );
};

const checkboxOne = styled.input`
  width: 40px;
  height: 10px;
  background: #555;
  margin: 20px 80px;
  position: relative;
  border-radius: 3px;
  label {
    display: block;
    width: 16px;
    height: 16px;
    border-radius: 50%;

    transition: all .5s ease;
    cursor: pointer;
    position: absolute;
    top: -3px;
    left: -3px;

    background: #ccc;
}
`;
export default ListTodos;
